function SqlSaveSystemLogPoints(gameTable , type, score , rank)
    local sql = string.format("insert into zzlog_statistic_points (server_ip, game_type, time, type, score, rank) values ('%s',%d, '%s', '%s', %d ,'%s')",
            ServerIP_Port  , gameTable.GameID, GetOsDateNow(), type,score ,rank)

    --print("保存小奖池"..rank)
    ZMySqlExec(sql) -- 性能快
end
