----------------------分数---------------------------------------
function ScoreAdd(player, llScore)
    player.User.Score = player.User.Score + llScore
end

function ScoreReduce(player, llScore)
    player.User.Score = player.User.Score - llScore
end

function ScoreGet(player)
    return player.User.Score
end
