--- 获取随机数
function GetRandom(min,max)
    math.randomseed(GetOsTimeMillisecond())
    return math.random(min,max)  -- [min,max]
end

---获取百分比方法， 比如10几率， 那么小于等于10，返回true
function ZRandomPercentRate(rate)
    local rr = GetRandom(1, 100)
    if rr <= rate then
        return true
    else
        return false
    end
end


---获取万分比方法， 比如10几率， 那么小于等于10，返回true
function ZRandomMillionRate(rate)
    local rr = GetRandom(1, 10000)
    if rr <= rate then
        return true
    else
        return false
    end
end



--- 打乱数组的顺序
function ZRandomShuffle(t)
    local tab = {}
    local index = 1
    while #t ~= 0 do
        local n=math.random(1,#t)
        if t[n] ~= nil then
            tab[index] = t[n]
            table.remove(t,n)
            index = index + 1
        end
    end
    return tab
end
